package daily0403_ppt2;

public class CastingEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double k27_d = 85.4;// 선언 및 초기화
		int k27_score = (int) k27_d;// int형으로 형변환 후 대입
		System.out.println("score =" + k27_score);// 출력
		System.out.println("d=" + k27_d);// 출력

	}

}
