package daily0403_ppt2;

public class SpecialCharEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println('\'');// '''처럼 할수 없다.

		System.out.println("abc\t123\b456");// \b에 의해서 3이 지워진다.
		System.out.println('\n');// 개행(new line) 문자 출력하고 개행
		System.out.println("\"Hello\"");// 큰따옴표 출력
		System.out.println("c:\\");// 경로 적을 때 \\을 써서 은 한번만 출력
	}

}
